#ifdef __cplusplus
extern "C" {
#endif
void my_exit(int *status){
   int s = *status;
   exit(s);
}
void egs_exit(int *status)  {my_exit(status);}
void egs_exit_(int *status) {my_exit(status);}
void egs_exit__(int* status){my_exit(status);}
void EGS_EXIT(int *status)  {my_exit(status);}
void EGS_EXIT_(int *status) {my_exit(status);}
void EGS_EXIT__(int *status){my_exit(status);}
#ifdef __cplusplus
}
#endif

#include <stdio.h>
#include <stdlib.h>

int fsize(const char* fname) {
  FILE* fp;
  long the_size;
  if((fp=fopen(fname, "rb")) == NULL ){
     printf("Error opening %s.\n", fname);
  }
  do {
   getc(fp);
  }while(!feof(fp));
  the_size = ftell(fp);
  fclose(fp);
  /*printf("%i\n", the_size);*/
  return the_size;
}
int file_size(  const char* fname, int len) {return fsize(fname);}
int file_size_( const char* fname, int len) {return fsize(fname);}
int file_size__(const char* fname, int len) {return fsize(fname);}
int FILE_SIZE(  const char* fname, int len) {return fsize(fname);}
int FILE_SIZE_( const char* fname, int len) {return fsize(fname);}
int FILE_SIZE__(const char* fname, int len) {return fsize(fname);}

#include <stdio.h>
void junk() {
   printf("lower case, ");
}
void junk_() {
   printf("lower case, _ ");
}
void JUNK() {
   printf("upper case, ");
}
void JUNK_() {
   printf("upper case, _ ");
}
void junk_junk() {
   printf("no underscores\n");
}
void junk_junk_() {
   printf("and _\n");
}
void junk_junk__() {
    printf("and __\n");
}
void JUNK_JUNK() {
   printf("no underscores\n");
}
void JUNK_JUNK_() {
    printf("and _\n");
}
void JUNK_JUNK__() {
    printf("and __\n");
}
